import React, { useState, useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import WaveSurfer from 'wavesurfer.js';
import './styles.css';

function InterviewPage() {
  const [userResponse, setUserResponse] = useState('');
  const [feedback, setFeedback] = useState('');
  const location = useLocation();  // Access the name passed from NameInputPage
  const interviewerWaveformRef = useRef(null);
  const intervieweeWaveformRef = useRef(null);
  const [isRecording, setIsRecording] = useState(false);
  const mediaRecorderRef = useRef(null);

  // Function to handle the response submission
  const handleSubmitResponse = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/interview', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          response: userResponse,
          name: location.state.name  // Pass the name and response to the backend
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to submit response');
      }

      const data = await response.json();  // Parse JSON response
      setFeedback(data.feedback);  // Set feedback received from the backend
    } catch (error) {
      console.error('Error submitting response:', error);
    }
  };

  useEffect(() => {
    // Initialize wavesurfer for interviewer with a default audio file
    const interviewerWaveform = WaveSurfer.create({
      container: interviewerWaveformRef.current,
      waveColor: '#4A90E2',
      progressColor: '#333',
      barWidth: 2,
      height: 100,
    });

    interviewerWaveform.load('/assets/demo.wav');  // Use default audio file
    return () => interviewerWaveform.destroy();
  }, []);

  const startRecording = async () => {
    // Set up media recording for interviewee
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const mediaRecorder = new MediaRecorder(stream);
    mediaRecorderRef.current = mediaRecorder;

    const intervieweeWaveform = WaveSurfer.create({
      container: intervieweeWaveformRef.current,
      waveColor: '#FF6347',
      barWidth: 2,
      height: 100,
    });

    const audioChunks = [];
    mediaRecorder.ondataavailable = (event) => {
      audioChunks.push(event.data);
    };

    mediaRecorder.onstop = () => {
      const audioBlob = new Blob(audioChunks);
      const audioUrl = URL.createObjectURL(audioBlob);
      intervieweeWaveform.load(audioUrl);  // Load the recorded audio
    };

    setIsRecording(true);
    mediaRecorder.start();
  };

  const stopRecording = () => {
    setIsRecording(false);
    mediaRecorderRef.current.stop();
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '20%' }}>
      <h2>Welcome, {location.state.name}. Let's start your interview!</h2>

      {/* Interviewer Section */}
      <h3>Interviewer</h3>
      <div ref={interviewerWaveformRef} style={{ width: '100%', marginBottom: '20px' }}></div>

      {/* Interviewee Section */}
      <h3>Interviewee</h3>
      <div ref={intervieweeWaveformRef} style={{ width: '100%', marginBottom: '20px' }}></div>
      
      {isRecording ? (
        <button onClick={stopRecording}>Stop Recording</button>
      ) : (
        <button onClick={startRecording}>Start Recording</button>
      )}

      {/* Text response and submission */}
      <textarea
        value={userResponse}
        onChange={(e) => setUserResponse(e.target.value)}
        placeholder="Type your response here"
      />
      <button onClick={handleSubmitResponse}>Submit Response</button>

      {feedback && <p>Feedback: {feedback}</p>}
    </div>
  );
}

export default InterviewPage;
