import React, { useState, useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import WaveSurfer from 'wavesurfer.js';

function InterviewPage() {
  const [userResponse, setUserResponse] = useState('');
  const [feedback, setFeedback] = useState('');
  const location = useLocation();
  const interviewerWaveformRef = useRef(null);
  const intervieweeWaveformRef = useRef(null);
  const [isRecording, setIsRecording] = useState(false);
  const mediaRecorderRef = useRef(null);
  const [recordedAudioUrl, setRecordedAudioUrl] = useState(null);

  useEffect(() => {
    // Initialize WaveSurfer for the interviewer
    const interviewerWaveform = WaveSurfer.create({
      container: interviewerWaveformRef.current,
      waveColor: '#4A90E2',
      progressColor: '#333',
      barWidth: 2,
      height: 100,
    });

    // Load the interviewer audio file and play it
    interviewerWaveform.load('/assets/demo.wav');
    interviewerWaveform.on('ready', () => {
      interviewerWaveform.play();
    });

    return () => interviewerWaveform.destroy(); // Cleanup
  }, []);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const mediaRecorder = new MediaRecorder(stream);
    mediaRecorderRef.current = mediaRecorder;

    const audioChunks = [];
    mediaRecorder.ondataavailable = (event) => {
      audioChunks.push(event.data);
    };

    mediaRecorder.onstop = () => {
      const audioBlob = new Blob(audioChunks);
      const audioUrl = URL.createObjectURL(audioBlob);
      setRecordedAudioUrl(audioUrl);

      // Initialize WaveSurfer for the interviewee with recorded audio
      const intervieweeWaveform = WaveSurfer.create({
        container: intervieweeWaveformRef.current,
        waveColor: '#FF6347',
        barWidth: 2,
        height: 100,
      });

      intervieweeWaveform.load(audioUrl);
    };

    setIsRecording(true);
    mediaRecorder.start();
  };

  const stopRecording = () => {
    setIsRecording(false);
    mediaRecorderRef.current.stop();
  };

  const handleSubmitResponse = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/interview', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          response: userResponse,
          name: location.state.name,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to submit response');
      }

      const data = await response.json();
      setFeedback(data.feedback);
    } catch (error) {
      console.error('Error submitting response:', error);
    }
  };

  return (
    <div className="text-center mt-20">
      <h2 className="text-4xl font-bold text-center mb-8">
        Welcome, {location.state.name}. Let's start your interview!
      </h2>

      {/* Interviewer Subtitle */}
      <h3 className="text-2xl font-semibold text-center mb-4">Interviewer</h3>
      <div ref={interviewerWaveformRef} className="w-full mb-5"></div>

      {/* Interviewee Subtitle */}
      <h3 className="text-2xl font-semibold text-center mb-4">Interviewee</h3>
      <div ref={intervieweeWaveformRef} className="w-full mb-5"></div>

      {isRecording ? (
        <button
          onClick={stopRecording}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Stop Recording
        </button>
      ) : (
        <button
          onClick={startRecording}
          className="bg-green-500 text-white px-4 py-2 rounded"
        >
          Start Recording
        </button>
      )}

      <textarea
        value={userResponse}
        onChange={(e) => setUserResponse(e.target.value)}
        placeholder="Type your response here"
        className="border border-gray-300 w-full mt-4 p-2"
      />
      <button
        onClick={handleSubmitResponse}
        className="bg-blue-500 text-white px-4 py-2 rounded mt-2"
      >
        Submit Response
      </button>

      {feedback && <p>Feedback: {feedback}</p>}
    </div>
  );
}

export default InterviewPage;
