import React, { useState, useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import WaveSurfer from 'wavesurfer.js';

function InterviewPage() {
  const [userResponse, setUserResponse] = useState('');
  const [feedback, setFeedback] = useState('');
  const [isRecording, setIsRecording] = useState(false); // For recording state
  const location = useLocation();
  const interviewerWaveformRef = useRef(null);
  const intervieweeWaveformRef = useRef(null);
  const mediaRecorderRef = useRef(null);

  useEffect(() => {
    const interviewerWaveform = WaveSurfer.create({
      container: interviewerWaveformRef.current,
      waveColor: '#4A90E2',
      progressColor: '#333',
      barWidth: 2,
      height: 100,
    });

    interviewerWaveform.load('/assets/demo.wav');  // Default audio
    return () => interviewerWaveform.destroy();
  }, []);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const mediaRecorder = new MediaRecorder(stream);
    mediaRecorderRef.current = mediaRecorder;

    const intervieweeWaveform = WaveSurfer.create({
      container: intervieweeWaveformRef.current,
      waveColor: '#FF6347',
      barWidth: 2,
      height: 100,
    });

    const audioChunks = [];
    mediaRecorder.ondataavailable = (event) => {
      audioChunks.push(event.data);
    };

    mediaRecorder.onstop = () => {
      const audioBlob = new Blob(audioChunks);
      const audioUrl = URL.createObjectURL(audioBlob);
      intervieweeWaveform.load(audioUrl);
    };

    setIsRecording(true); // Set recording state to true
    mediaRecorder.start();
  };

  const stopRecording = () => {
    setIsRecording(false); // Set recording state to false
    mediaRecorderRef.current.stop();
  };

  return (
    <div className="relative text-center mt-20">
      {/* Display the recording text only when recording */}
      {isRecording && (
        <div className="absolute top-0 left-0 text-red-500 font-bold animation-fade px-4 py-2">
          Recording Audio...
        </div>
      )}

      <h2 className="text-4xl font-bold">Welcome, {location.state.name}. Let's start your interview!</h2>

      <h3 className="text-2xl font-semibold mt-4">Interviewer</h3>
      <div ref={interviewerWaveformRef} className="w-full mb-5"></div>

      <h3 className="text-2xl font-semibold mt-4">Interviewee</h3>
      <div ref={intervieweeWaveformRef} className="w-full mb-5"></div>

      {isRecording ? (
        <button onClick={stopRecording} className="bg-red-500 text-white px-4 py-2 rounded">Stop Recording</button>
      ) : (
        <button onClick={startRecording} className="bg-green-500 text-white px-4 py-2 rounded">Start Recording</button>
      )}

      <textarea
        value={userResponse}
        onChange={(e) => setUserResponse(e.target.value)}
        placeholder="Type your response here"
        className="border border-gray-300 w-full mt-4 p-2"
      />
      <button className="bg-blue-500 text-white px-4 py-2 rounded mt-2">Submit Response</button>

      {feedback && <p>Feedback: {feedback}</p>}
    </div>
  );
}

export default InterviewPage;
